props.code:
virtualisation.lxd.enable = true;
virtualisation.lxc.lxcfs.enable = true;
virtualisation.lxd.recommendedSysctlSettings = true;

# Allow your user to use LXD
users.users.<your user>.extraGroups = [ "lxd" ];

# optional but recommended:
nix.settings = {
  extra-substituters = [ "https://codchi.cachix.org" ];
  trusted-public-keys = [ "codchi.cachix.org-1:dVwdzogJgZO2x8kPKW02HNt2dpd/P/z46pY465MkokY=" ];
};

# If you use a local DNS server. See https://github.com/NixOS/nixpkgs/issues/263359
firewall.interfaces.lxdbr0.allowedTCPPorts = [ 53 ];
firewall.interfaces.lxdbr0.allowedUDPPorts = [ 53 67 ];

{
    programs.bash.enableBashCompletion = true;
    programs.zsh.enableBashCompletion = true;
    programs.fish.vendor.completions.enable = true;
}

{ pkgs, inputs, ... }: {
    programs.nushell.extraConfig = ''
        source ${pkgs.runCommand "codchi-nushell-complete" {} ''
          ${inputs.codchi.packages.${pkgs.system}.default}/bin/codchi complete nushell > $out
        ''}
    '';
}

inputs.codchi.url = "github:aformatik/codchi";
inputs.codchi.nixpkgs.follows = "nixpkgs";

outputs = inputs@{ ... }: {
    # Pass inputs to your NixOS configuration (e.g. via extraArgs)
};

{
  description = "My awesome code machine";
  outputs = { ... }: {
    nixosModules.myModule = ./configuration.nix;
  };
}

# configuration.nix
{
  codchi.secrets.env.TEST = {
      description = ''
        This is an example secret. Please enter a random value!
      '';
  };
}

# configuration.nix
{ 
  systemd.services.my-secret-service = {
    description = "My Service which reads Codchi's secrets";
    wantedBy = [ "multi-user.target" ];

    script = ''
      source /etc/codchi-env
      echo "Got secret CODCHI_TEST=$CODCHI_TEST from codchi"
    '';
  };
}

{
  environment.etc = {
    hosts.enable = true; # /etc/hosts set by Codchi
    "resolv.conf".enable = false; # /etc/hosts set by WSL
  };
}

{ pkgs, ... }: {

  # Direnv support
  direnv = {
    enable = true;
    enableBashIntegration = true;
    # use_nix and use_flake support
    nix-direnv.enable = true;
  };

  # See <https://codchi.dev/config/editor> on how to add an editor
}

{ pkgs, ... }: {

  # For direnv support
  # direnv = {
  #   enable = true;
  #   enableBashIntegration = true;
  #   # use_nix and use_flake support
  #   nix-direnv.enable = true;
  # };

  environment.systemPackages = [
    pkgs.devenv
    # Or see <https://flox.dev/docs/install-flox/#install-flox-from-scratch> on how to install flox
  ];

  # See <https://codchi.dev/config/editor> on how to add an editor
}

{ pkgs, ... }: {
  environment.systemPackages = [
    # Plain VSCode
    pkgs.vscodium

    # With plugins
    (pkgs.vscode-with-extensions.override {
      vscode = pkgs.vscodium;
      vscodeExtensions = with vscode-extensions; [
        bbenoist.nix
        ms-python.python
        ms-azuretools.vscode-docker
        ms-vscode-remote.remote-ssh
      ] ++ pkgs.vscode-utils.extensionsFromVscodeMarketplace [
        {
          name = "remote-ssh-edit";
          publisher = "ms-vscode-remote";
          version = "0.47.2";
          sha256 = "1hp6gjh4xp2m1xlm1jsdzxw9d8frkiidhph6nvl24d0h8z34w49g";
        }
      ];
    })

    # FHS env
    pkgs.vscodium-fhs
  ];
}

{ pkgs, ... }: {

  # Setup JDKs if neccessary

  environment.systemPackages = [
    pkgs.jetbrains.idea-community

    pkgs.jetbrains.idea-ultimate

    # With plugins
    (with pkgs.jetbrains; plugins.addPlugins pycharm-community [
      "nixidea"
      "ideavim"
    ])

  ];

  # For proprietary apps like IntelliJ Ultimate
  nixpkgs.config.allowUnfree = true;
}

{ pkgs, ... }: {
  programs.npm = {
    enable = true;
    # If you want a specific version
    # package = pkgs.nodePackages_13_x.npm;

    # Configure npm if needed:
    # npmrc = ''
    #   prefix = ''${HOME}/.npm
    #   https-proxy=proxy.example.com
    #   init-license=MIT
    #   init-author-url=https://www.npmjs.com/
    #   color=true
    # '';
  };

  environment.systemPackages = [
    pkgs.nodejs
    # or a specific version:
    # pkgs.nodejs_22

    # Deno:
    pkgs.deno

    # Bun:
    pkgs.bun

    # Other tools:
    # pkgs.biome
    # pkgs.nodePackages.prettier

    # Editor:
    # See <https://codchi.dev/config/editor> for more info
    pkgs.vscodium
    # Or, if you have problems with manually installed plugins:
    # pkgs.vscodium-fhs
  ];
}

{ pkgs, ... }: {
  programs.java = {
    enable = true;
    # Set default JDK
    package = pkgs.jdk21;
    packages = {
      # Additional JDKs which get symlinked to ~/.jdks/<attribute name> (for e.g. IntelliJ)
      openjdk8 = pkgs.jdk8;
      # ...
    };
  };


  environment.systemPackages = [
    pkgs.maven
    # If you want gradle globally installed
    # pkgs.gradle

    # IntelliJ Community
    # pkgs.jetbrains.idea-community

    # You can also install IJ with plugins (experimental).
    # For more information see <https://github.com/NixOS/nixpkgs/tree/nixos-24.05/pkgs/applications/editors/jetbrains>
    (with pkgs.jetbrains; plugins.addPlugins idea-community [
      "nixidea"
      "ideavim"
    ])

  ];

  # For proprietary apps like IntelliJ Ultimate
  # nixpkgs.config.allowUnfree = true;
}

{ pkgs, ... }: {
  # Will be recognized by IntelliJ
  environment.systemPackages = [ pkgs.scala pkgs.sbt ];
}

{ pkgs, ... }: {
  environment.systemPackages = [ 
    pkgs.eclipses.eclipse-java
    # for JEE
    # pkgs.eclipses.eclipse-jee 
  ];
}

{ pkgs, ... }: {
  environment.systemPackages = with pkgs; [
    # Formatter
    pkgs.nixpkgs-fmt

    # VSCode
    (vscode-with-extensions.override {
      vscodeExtensions = with vscode-extensions; [
        bbenoist.nix
      ];
    })

    # A language server if you have an editor with LSP support:
    pkgs.nixd
    # or
    pkgs.nil
  ];
}

{ pkgs, ... }: {
  environment.systemPackages = [
    pkgs.python39
    # Pip on NixOS only works in virtualenv
    pkgs.python39Packages.pip
    pkgs.python39Packages.virtualenv

    # Editor:
    # See <https://codchi.dev/config/editor> for more info
    pkgs.jetbrains.pycharm-community
    # Or VSCode(ium)
    (pkgs.vscode-with-extensions.override {
      vscode = pkgs.vscodium;
      vscodeExtensions = with pkgs.vscode-extensions; [
        ms-python.python
      ];
    })
  ];
}

{ pkgs, ... }:
{
  environment.systemPackages = [
    (pkgs.vscode-with-extensions.override {
      vscode = pkgs.vscodium;
      vscodeExtensions = with pkgs.vscode-extensions; [
        rust-lang.rust-analyzer
        jnoortheen.nix-ide
        mkhl.direnv
        asvetliakov.vscode-neovim
      ];
    })
    pkgs.bashInteractive # fix terminal in VSCode
  ];
  programs.neovim.enable = true; # needed for vscode-neovim plugin
  programs.direnv = {
    enable = true;
    nix-direnv.enable = true;
  };
}

{ pkgs, ... }: {

  # https://github.com/oxalica/rust-overlay
  nixpkgs.overlays = [
    (self: super:
      let
        overlay = super.fetchFromGitHub {
          repo = "rust-overlay";
          owner = "oxalica";
          sha256 = "1bp1k5qla5gwh6vc50m5pcwdfxn6g703yx1i8qrjs4l7kgh3y507";
          rev = "573c674a3ad06e8a525263185ebef336a411d1d5";
        };
      in
      {
        inherit (import overlay self super) rust-bin;
      }
    )
  ];

  environment.systemPackages = [

    pkgs.gcc

    # https://github.com/oxalica/rust-overlay
    (
      let
        rustConfig = {
          extensions = [
            "rust-src"
            "rust-analyzer"
          ];
          targets = [
            "x86_64-unknown-linux-gnu"
            "wasm32-wasi"
          ];
        };
      in
      # stable
      pkgs.rust-bin.stable.latest.default.override rustConfig
      # nightly
      # pkgs.rust-bin.selectLatestNightlyWith (toolchain: toolchain.default.override rustConfig);
    )

    # You can also install IJ with plugins (experimental).
    # For more information see <https://github.com/NixOS/nixpkgs/tree/nixos-24.05/pkgs/applications/editors/jetbrains>
    (with pkgs.jetbrains; plugins.addPlugins rust-rover [
      "nixidea"
    ])

    # When using VSCode instead of RustRover
    # (pkgs.vscode-with-extensions.override {
    #   vscode = pkgs.vscodium;
    #   vscodeExtensions = with pkgs.vscode-extensions; [
    #     rust-lang.rust-analyzer
    #     jnoortheen.nix-ide
    #   ];
    # })
    # pkgs.bashInteractive # fix terminal in VSCode
  ];

  # For proprietary apps like RustRover
  nixpkgs.config.allowUnfree = true;
}

